import logo from './logo.svg';
import { useState } from 'react';
import './App.css';
function Header(props) {
  return <h1><a href="/" onClick={(event) => {
    event.preventDefault();
    props.onChangeMode();
  }} >{props.title}</a></h1>

}
function Nav(props) {
  const lis = [];
  for (let i = 0; i < props.topics.length; i++) {
    let t = props.topics[i];
    lis.push(<li key={t.id}> <a id={t.id} href={'/read/' + t.id} onClick={(event) => {
      event.preventDefault();
      props.onChangeMode(event.target.id);
    }}> {t.title}</a></li>)
  }
  return lis
}
function Article(props) {
  return <article>
    <h2>{props.title}</h2>
    {props.body}
  </article>
}
function Create(props) {
  return <a href="/create" onClick={(event) => {
    event.preventDefault();
    props.onChangeMode();
  }} >create</a>

}
function App() {
  const [mode, setMode] = useState("Title");
  const [id, setId] = useState(null);
  const topic = [
    { id: 1, title: "html", body: "htlml is " },
    { id: 2, title: "css", body: "css is " },
    { id: 3, title: "js", body: "js is " },

  ]
  const [topics, setTopic] = useState(topic);
  let content = "";
  if (mode === "Title") {
    content = <Article title="welcome" body="hello, web" ></Article>;
  } else if (mode === "topics") {
    for (let i = 0; i < topics.length; i++) {
      if (topics[i].id === Number(id)) {
        content = <Article title={topics[i].title} body={topics[i].body} ></Article>;
      }
    }
  } else if (mode === "Create") {
    content = <div><h3>Create</h3><input type="text" id="inputValue"></input> <br></br>
      <textarea id="textarea"></textarea><br></br><button onClick={() => {
        let _id = topics.length+ 1;
        setTopic(
          ...topics, {
          id: _id, title: document.getElementById("inputValue").value, body: document.getElementById("textarea").value
        }
        )
        setId(_id);
        setMode("topics");
      }}>create</button></div>;
  }
  return (
    <div>
      <Header title="REACT" onChangeMode={() => {
        setMode("Title");
      }}></Header>
      <Header></Header>
      <Nav topics={topics} onChangeMode={(id) => {
        setId(id);
        setMode("topics")
      }}></Nav>
      {content}
      <Create onChangeMode={() => {
        setMode("Create")
      }}></Create>    </div>
  );
}

export default App;
