import logo from './logo.svg';
import React, { useState } from 'react';
import {Link, Route, Routes, Router, BrowserRouter} from "react-router-dom"
import styled from 'styled-components'
import './App.css';
f
function App() {
  const PrimaryReactButton = styled.button`
    color:${props=> props.primary ? 'white' : 'black'};
    background-color: ${props => props.primary ? 'blue' : 'green'};
    `
  const SimpleButtom = styled.button`
  color:white;
  background-color : green;
  `;
  const LargeButton = styled(SimpleButtom)`
    font-size:50px;`
  ;
  const ReactButton = props =>{
    return <button className={props.className}>{props.children}</button>
  }
  const LargeReactButton = styled(ReactButton)`
  font-size:50px;
  `;

  const [mode, setMode] = useState("Title");
  const [id, setId] = useState(null);
  const topic = [
    { id: 1, title: "html", body: "htlml is " },
    { id: 2, title: "css", body: "css is " },
    { id: 3, title: "js", body: "js is " },

  ]
  let contextControl = null;
  const [nextId, setNextId] = useState(4);
  const [topics, setTopic] = useState(topic);
  let content = "";
  if (mode === "Title") {
    content = <Article title="welcome" body="hello, web" ></Article>;
  } else if (mode === "topics") {
    for (let i = 0; i < topics.length; i++) {
      if (topics[i].id === Number(id)) {
        content = <Article title={topics[i].title} body={topics[i].body} ></Article>;
      }
    }
    contextControl = <><li><a href={"/update/" + id} onClick={(event) => {
      event.preventDefault();
      setMode("UPDATE")
    }}>Update</a></li>
      <li> <button onClick={event => {
        let newtopocs = [];
        for (let i = 0; i < topics.length; i++) {

          if (topics[i].id !== id) {
            newtopocs.push(topics[i]);
          }
        }
        setTopic(newtopocs)
        setMode("Title")
      }}>delete</button></li>
    </>
  } else if (mode === "Create") {
    content = <Create onChangeMode={(title, body) => {
      const newTopic = { id: nextId, title: title, body: body }
      const newTopics = [...topics]
      newTopics.push(newTopic);
      setTopic(newTopics);
      setId(nextId);
      setNextId(nextId + 1);
      setMode("topics")
    }}></Create>
  } else if (mode === "UPDATE") {
    let title = "";
    let body = "";
    for (let i = 0; i < topics.length; i++) {
      if (topics[i].id === Number(id)) {
        title = topics[i].title;
        body = topics[i].body;
      }
    }
    content = <Update title={title} body={body} onUpdate={(title, body) => {
      const newTopics = [...topics];
      const updatedTopic = { id: id, title: title, body: body }
      for (let i = 0; i < newTopics.length; i++) {
        if (newTopics[i].id === id) {
          newTopics[i] = updatedTopic;
          setTopic(newTopics);
          break;
        }
      }
      setMode("topics");
    }}></Update>
  }
  return (      <BrowserRouter>

    <div>
      <Header title="REACT" onChangeMode={() => {
        setMode("Title");
      }}></Header>
      <Header></Header>
      <Nav topics={topics} onChangeMode={(id) => {
        setId(id);
        setMode("topics")
      }}></Nav>
      {content}
      <ul>
        <li>    <a href="/create" onClick={
          (event) => {
            event.preventDefault();
            setMode("Create");
          }
        }>create</a></li>
        {contextControl}
      </ul>
      <Linkist></Linkist>
<Routes>
  <Route path="/first" element={"home page"}></Route>
  <Route path="/second" element={"second page"}></Route>
  <Route path="/third" element={"third page"}></Route>
</Routes>
    </div>
    <SimpleButtom>simple</SimpleButtom>
    <LargeButton>large</LargeButton>
    <LargeReactButton>react</LargeReactButton>
    <PrimaryReactButton>normal</PrimaryReactButton>
    <PrimaryReactButton primary>primary</PrimaryReactButton>
    </BrowserRouter>

  );
}

export default App;
