import React, { useReducer ,useState, createContext, useContext} from 'react';
import { Link, Route, Routes, Router, BrowserRouter } from "react-router-dom"
import styled from 'styled-components'
import './App.css';
import './style.css'
const themeDefault = {border: '10px solid green'};
const themeContext = createContext(themeDefault);
function App() {
  const theme = useContext(themeContext)
  console.log('theme' , theme);
  return (    <themeContext.Provider value ={{border:'10px solid yellow'}}>

<div className='root' style={theme}>
  <h1>Hello World</h1>
  <Sub1/>
</div></themeContext.Provider>
  )
}

function Sub1(){
  const theme = useContext(themeContext);
  return (
    <themeContext.Provider value ={{border:'10px solid red'}}>
    <div style={theme}>
      <h1>Sub1</h1>
      <Sub2></Sub2>
    </div></themeContext.Provider>
  )
}
function Sub2(){  const theme = useContext(themeContext);

  return (
    <div style={theme}>  
      < h1>Sub2</h1>
      <Sub3/>
    </div>
  )
}
function Sub3(){  const theme = useContext(themeContext);

  return (
    <div style={theme}>
      <h1>Sub3</h1>
    </div>
  )
}
export default App;
